    meta: { showSidebar: false },
  },

  // Â≠¶ÁîüË∑ØÁî±
  {
    path: "/student",
    component: StudentLayout,
    children: [
      {
        path: "profile",
        name: "StudentProfile",
        component: () => import("../views/Student/stuProfile.vue"),
        meta: { showSidebar: false },
