    var menuConfig = {
      teaching: [{
        name: '教学管理',
        children: [{
          name: '考勤管理',
          path: '/student/attendance'
        }, {
          name: '作业管理',
          path: '/student/homework'
        }, {
          name: '作业详情',
          path: '/student/homeworkdetail'
        }, {
          name: '成绩查询',
          path: '/student/grade'
        }, {
          name: '课程资源',
          path: '/student/classresource'
        }, {
          name: '请假管理',
          path: '/student/leave'
        }]
      }],
      resource: [{
        name: '资源管理',
        children: [{
          name: '教室预约',
          path: '/student/classroom'
        }, {
          name: '书籍借阅',
          path: '/student/library'
        }]
      }]
    };

    // 计算当前模块的菜单
    var currentMenuGroups = computed(function () {
      var path = route.path;

      // 教学管理路由
      if (path.startsWith('/student/attendance') || path.startsWith('/student/homework') || path.startsWith('/student/homeworkdetail') || path.startsWith('/student/grade') ||
      // 修正：grade 不是 grades
      path.startsWith('/student/classresource') ||
      // 修正：classresource 不是 resources
      path.startsWith('/student/leave')) {
        return menuConfig.teaching;
      }
      // 资源管理路由
      else if (path.startsWith('/student/classroom') || path.startsWith('/student/library')) {
        return menuConfig.resource;
      }
      return [];
    });

    // 计算当前模块标题
    var currentModuleTitle = computed(function () {
      var path = route.path;
      if (path.startsWith('/student/attendance') || path.startsWith('/student/homework') || path.startsWith('/student/homeworkdetail') || path.startsWith('/student/grade') || path.startsWith('/student/classresource') || path.startsWith('/student/leave')) {
        return '教学管理';
      } else if (path.startsWith('/student/classroom') || path.startsWith('/student/library')) {
        return '资源管理';
      }
      return '功能菜单';
    });

    // 检查菜单项是否激活
    var isItemActive = function isItemActive(item) {
      return route.path === item.path || route.path.startsWith(item.path + '/');
    };
    return {
      currentMenuGroups: currentMenuGroups,
      currentModuleTitle: currentModuleTitle,
      isItemActive: isItemActive
    };
  }
};
