// router/index.js
import { createRouter, createWebHistory } from 'vue-router'

const routes = [
  /*
  {
    path: '/',
    name: 'Home',
    redirect: '/resource-home'  
  },
  */

  // 学生路由
  {
    path: '/student',
    meta: { role: 'student' },
    children: [
      {
        path: 'home',
        name: 'StudentHome',
        component: () => import('../views/Student/StuResourceHome.vue'),
        meta: { showSidebar: false }
      },
      {
        path: 'library',
        name: 'StudentLibrary',
        component: () => import('../views/Student/Resource/StudentLibrary.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'classroom',
        name: 'StudentClassroom',
        component: () => import('../views/Student/Resource/TeaStuClassroom.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'profile',
        name: 'StudentProfile',
        component: () => import('../views/Student/stuProfile.vue'),
        meta: { showSidebar: false }
      },
      /*
      {
        path: 'dorm',
        name: 'StudentDorm',
        component: () => import('../views/Student/Campus/StudentDorm.vue'),
        meta: { showSidebar: true }
      }*/
    ]
  },

  // 教师路由
  {
    path: '/teacher',
    meta: { role: 'teacher' },
    children: [
      {
        path: 'resourcehome',
        name: 'TeacherHome',
        component: () => import('../views/Teacher/TeaResourceHome.vue'),
        meta: { showSidebar: false }
      },
      {
        path: 'library',
        name: 'TeacherLibrary',
        component: () => import('../views/Teacher/Resource/TeacherLibrary.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'classroom',
        name: 'TeacherClassroom',
        component: () => import('../views/Teacher/Resource/TeaStuClassroom.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'profile',
        name: 'TeacherProfile',
        component: () => import('../views/Teacher/TeacherProfile.vue'),
        meta: { showSidebar: false }
      }
    ]
  },

  // 管理员路由
  {
    path: '/admin',
    meta: { role: 'admin' },
    children: [
      {
        path: 'home',
        name: 'AdminHome',
        component: () => import('../views/Admin/AdminResourceHome.vue'),
        meta: { showSidebar: false }
      },
      {
        path: 'dorm',
        name: 'AdminDorm',
        component: () => import('../views/Admin/Campus/AdminDorm.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'approve',
        name: 'AdminApprove',
        component: () => import('../views/Admin/Resource/AdminApprove.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'classroom',
        name: 'AdminClassroom',
        component: () => import('../views/Admin/Resource/AdminClassroom.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'library',
        name: 'AdminLibrary',
        component: () => import('../views/Admin/Resource/AdminLibrary.vue'),
        meta: { showSidebar: true }
      },
      {
        path: 'profile',
        name: 'AdminProfile',
        component: () => import('../views/Admin/AdminProfile.vue'),
        meta: { showSidebar: false }
      }
    ]
  },

  // 兼容旧路由（重定向到新路由）
  /*
  {
    path: '/student-library',
    redirect: '/student/library'
  },
  {
    path: '/tea-stu-classroom',
    redirect: '/student/classroom'
  },
  {
    path: '/stu-profile',
    redirect: '/student/profile'
  },

  // 404 页面
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: () => import('../views/NotFound.vue')
  }
  */
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  // 获取用户角色（从Vuex或localStorage）
  const userRole = localStorage.getItem('userRole') || 'student'

  // 检查路由权限
  if (to.meta.role && to.meta.role !== userRole) {
    // 无权限访问，重定向到首页或权限错误页面
    next('/resource-home')
    return
  }

  next()
})

export default router
