    path: "/",
    redirect: "/home",
  },
  {
    path: "/login",
    name: "Login",
    component: () => import("../views/Login.vue"), // ÊàñËÄÖÁõ¥Êé•Áî® component: Login
    // üí° Âª∫ËÆÆÊ∑ªÂä† meta Â≠óÊÆµÊòéÁ°ÆÊ†áËÆ∞‰∏çÈúÄË¶ÅÂ∏ÉÂ±Ä
    meta: { showLayout: false },
  },
  {
    path: "/home",
    name: "Home",
    component: () => import("../views/Home.vue"),
    meta: { showSidebar: false },
  },

  // Â≠¶ÁîüË∑ØÁî±
  {
    path: "/student",
    component: StudentLayout,
    children: [
      {
        path: "profile",
        name: "StudentProfile",
