        path: "teachinghome",
        name: "StudentTeachingHome",
        component: () =>
          import("../views/Student/Teaching/StuTeaching_Index.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "attendance",
        name: "StudentAttendance",
        component: () => import("../views/Student/Teaching/StuAttendance.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "grade",
        name: "StudentGrade",
        component: () => import("../views/Student/Teaching/StuGrades.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "homework",
        name: "StudentHomework",
        component: () => import("../views/Student/Teaching/StuHomework.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "homeworkdetail",
        name: "StudentHomeworkDetail",
        component: () =>
          import("../views/Student/Teaching/StuHomework_Detail.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "leave",
        name: "StudentLeave",
        component: () => import("../views/Student/Teaching/StuLeave.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "classresource",
        name: "StudentClassResource",
        component: () =>
          import("../views/Student/Teaching/StuClassResource.vue"),
        meta: { showSidebar: true },
      },

      // Â≠¶ÁîüËµÑÊ∫êÁõ∏ÂÖ≥Ë∑ØÁî±
      {
        path: "resourcehome",
        name: "StudentResourceHome",
        component: () =>
          import("../views/Student/Resource/StuResourceHome.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "library",
        name: "StudentLibrary",
        component: () => import("../views/Student/Resource/StudentLibrary.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "classroom",
        name: "StudentClassroom",
        component: () => import("../views/Student/Resource/StuClassroom.vue"),
        meta: { showSidebar: true },
      },
      // Â≠¶ÁîüÊ†°Âõ≠Áõ∏ÂÖ≥Ë∑ØÁî±
      {
        path: "campushome",
        name: "StudentCampusHome",
        component: () => import("../views/Student/Campus/StuCampusHome.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "studorm",
        name: "StudentDormAssign",
        component: () => import("../views/Student/Campus/StuDormAssign.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "forum",
        name: "StudentForum",
        component: () => import("../views/Student/Campus/StuForum.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "postdetail",
        name: "StudentPostDetail",
        component: () => import("../views/Student/Campus/StuPostDetail.vue"),
        meta: { showSidebar: false },
      },
    ],
  },

  // ÊïôÂ∏àË∑ØÁî±
  {
    path: "/teacher",
    component: TeacherLayout,
    children: [
      {
        path: "profile",
        name: "TeacherProfile",
        redirect: "/teacher/teachinghome", 
        component: () => import("../views/Teacher/TeacherProfile.vue"),
        meta: { showSidebar: false },
      },
      // ÊïôÂ∏àÊïôÂ≠¶ÂäüËÉΩË∑ØÁî±
      {
        path: "teachinghome",
        name: "TeacherTeachingIndex",
        component: () =>
          import("../views/Teacher/Teaching/Teateaching_Index.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "attendance",
        name: "TeacherAttendance",
        component: () => import("../views/Teacher/Teaching/TeaAttendance.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "grades",
        name: "TeacherGrades",
        component: () => import("../views/Teacher/Teaching/TeaGrades.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "homework",
        name: "TeacherHomework",
        component: () => import("../views/Teacher/Teaching/TeaHomework.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "resources",
        name: "TeacherResources",
        component: () => import("../views/Teacher/Teaching/TeaResources.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "students",
        name: "TeacherStudents",
        component: () => import("../views/Teacher/Teaching/TeaStudents.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "leave",
        name: "TeacherLeave",
        component: () => import("../views/Teacher/Teaching/TeachingLeave.vue"),
        meta: { showSidebar: true },
      },

      // ÊïôÂ∏àËµÑÊ∫êÁõ∏ÂÖ≥Ë∑ØÁî±
      {
        path: "resourcehome",
        name: "TeacherHome",
        component: () =>
          import("../views/Teacher/Resource/TeaResourceHome.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "library",
        name: "TeacherLibrary",
        component: () => import("../views/Teacher/Resource/TeacherLibrary.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "classroom",
        name: "TeacherClassroom",
        component: () => import("../views/Teacher/Resource/TeaClassroom.vue"),
        meta: { showSidebar: true },
      },
    ],
  },

  // ÁÆ°ÁêÜÂëòË∑ØÁî±
  {
    path: "/admin",
    component: AdminLayout,
    children: [
      // ÁÆ°ÁêÜÂëòÊ†°Âõ≠Áõ∏ÂÖ≥Ë∑ØÁî±
      {
        path: "campushome",
        name: "AdminCampusHome",
        component: () => import("../views/Admin/Campus/AdmCampusHome.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "dorm",
        name: "AdminDorm",
        component: () => import("../views/Admin/Campus/AdminDorm.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "dormassign",
        name: "DormAssign",
        component: () => import("../views/Admin/Campus/AdmDormAssign.vue"),
        meta: { showSidebar: false },
      },
      // ÁÆ°ÁêÜÂëòËµÑÊ∫êÁõ∏ÂÖ≥Ë∑ØÁî±
      {
        path: "resourcehome",
        name: "AdminHome",
        component: () =>
          import("../views/Admin/Resource/AdminResourceHome.vue"),
        meta: { showSidebar: false },
      },
      {
        path: "approve",
        name: "AdminApprove",
        component: () => import("../views/Admin/Resource/AdminApprove.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "classroom",
        name: "AdminClassroom",
        component: () => import("../views/Admin/Resource/AdminClassroom.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "library",
        name: "AdminLibrary",
        component: () => import("../views/Admin/Resource/AdminLibrary.vue"),
        meta: { showSidebar: true },
      },
      {
        path: "profile",
        name: "AdminProfile",
        component: () => import("../views/Admin/AdminProfile.vue"),
        meta: { showSidebar: false },
      },
    ],
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// üîí ÂÖ®Â±ÄÂâçÁΩÆÂÆàÂç´
router.beforeEach((to, from, next) => {
  // 1. Ëé∑Âèñ Token (ÂÅáËÆæÁôªÂΩïÂêéÂ≠òÂú® localStorage ‰∏≠)
  const token = localStorage.getItem("token");

  // 2. ÂÆö‰πâÊó†ÈúÄÁôªÂΩïÁöÑÁôΩÂêçÂçïÈ°µÈù¢ (ÊØîÂ¶ÇÁôªÂΩïÈ°µ„ÄÅÊ≥®ÂÜåÈ°µ)
  const whiteList = ["/login", "/register"];

  // 3. Âà§Êñ≠ÈÄªËæë
  if (whiteList.includes(to.path)) {
    // Â¶ÇÊûúË¶ÅÂéªÁôΩÂêçÂçïÈ°µÈù¢ÔºåÁõ¥Êé•ÊîæË°å
    next();
  } else {
    // Â¶ÇÊûúË¶ÅÂéªÂÖ∂‰ªñÈ°µÈù¢
    if (token) {
      // Êúâ TokenÔºåÊîæË°å
      next();
    } else {
      // Ê≤° TokenÔºåÂº∫Âà∂Ë∑≥ËΩ¨Âà∞ÁôªÂΩïÈ°µ
      next("/login");
    }
  }
});

export default router
